#intro scene ,called by main()
def intro_scene(dimx, dimy, cellsize):
    pygame.init()

    window_surface = pygame.display.set_mode((dimx * cellsize, dimy * cellsize))

    #set title
    pygame.display.set_caption("Conway's Game of Life")

    # fill the backgroung with intro scene
    bg_img = pygame.image.load('Image/bk9.jpg')
    bg_img = pygame.transform.scale(bg_img,(dimx*8,dimy*8))
   
    # blit 用來把其他元素渲染到另外一個 surface 上
    window_surface.blit(bg_img,(0,0))

    # realize game update by pygame method 
    pygame.display.update()

    #waiting for press space to get into main() (start the game)
    while True:
        for event in pygame.event.get():
            if event.type == pygame.KEYDOWN:
                if event.key == pygame.K_SPACE:
                    return


